// Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Box, List, ListItem, ListItemText } from '@mui/material';

function Sidebar() {
  const location = useLocation();

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '250px',
        height: '100%',
        backgroundColor: 'black',
        color: 'white',
        zIndex: 1, // Ensure sidebar stays on top of other content
      }}
    >
      <List>
        <ListItem button component={Link} to="/" sx={location.pathname === '/' ? { backgroundColor: 'rgb(188, 120, 255)' } : {}}>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem button component={Link} to="/profile" sx={location.pathname === '/profile' ? { backgroundColor: 'rgb(188, 120, 255)' } : {}}>
          <ListItemText primary="Profile" />
        </ListItem>
      </List>
    </Box>
  );
}

export default Sidebar;
