import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import Sidebar from './components/Sidebar';
import Home from './pages/Dashboard';
import Profile from './pages/Profile';
import LoginPage from './pages/Login';

const theme = createTheme();

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status

  const handleLogin = () => {
    // For now, let's use a static username and password
    const validUsername = 'user';
    const validPassword = 'password';

    // Check if entered username and password match
    if (username === validUsername && password === validPassword) {
      // Set login status to true
      setIsLoggedIn(true);
    } else {
      // Display an error message or handle invalid credentials
      alert('Invalid username or password');
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Router>
        {isLoggedIn ? (
          <div style={{ display: 'flex' }}>
            <Sidebar />
            <div style={{ flex: 1, paddingLeft: '250px' }}>
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/profile" element={<Profile />} />
              </Routes>
            </div>
          </div>
        ) : (
          // Render LoginPage component if not logged in
          <Routes>
            <Route path="/" element={<Navigate to="/login" />} />
            <Route path="/login" element={<LoginPage onLogin={handleLogin} />} /> {/* Pass handleLogin function to LoginPage */}
          </Routes>
        )}
      </Router>
    </ThemeProvider>
  );
}

export default App;
