// Sidebar.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Box, List, ListItem, ListItemIcon, ListItemText, Avatar } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import AccountCircleIcon from '@mui/icons-material/AccountCircle'; // Import the AccountCircle icon

function Sidebar() {
  const location = useLocation();

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '250px',
        height: '100%',
        backgroundColor: 'black',
        color: 'white',
        zIndex: 1, // Ensure sidebar stays on top of other content
      }}
    >
      <List>
        <ListItem button component={Link} to="/">
          <Avatar sx={{ backgroundColor: location.pathname === '/' ? 'rgb(188, 120, 255)' : 'transparent' }}>
            <HomeIcon />
          </Avatar>
          <ListItemText primary="Home" />
        </ListItem>
        <ListItem button component={Link} to="/profile">
          <Avatar sx={{ paddingLeft: '10px',backgroundColor: location.pathname === '/profile' ? 'rgb(188, 120, 255)' : 'transparent' }}>
            <AccountCircleIcon />
          </Avatar>
          <ListItemText primary="Profile" />
        </ListItem>
      </List>
    </Box>
  );
}

export default Sidebar;
